import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card } from '@mui/material';

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/transactions', { headers: { Authorization: `Bearer ${token}` } });
        setTransactions(res.data.transactions);
      } catch (err) {
        console.error(err);
      }
    };
    fetchTransactions();
  }, []);

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <Card style={{ width: 400 }}>
        <h2>Transactions</h2>
        <ul>
          {transactions.map(tx => (
            <li key={tx._id}>
              <strong>Type:</strong> {tx.type} <br />
              <strong>Amount:</strong> {tx.amount} <br />
              <strong>From:</strong> {tx.sender.name} <br />
              <strong>To:</strong> {tx.receiver.name} <br />
              <strong>Date:</strong> {new Date(tx.createdAt).toLocaleString()}
            </li>
          ))}
        </ul>
      </Card>
    </div>
  );
};

export default Transactions;
