export var THROTTLE_MS = 500;
export function throttle(fn, throttleMs) {
    if (throttleMs === void 0) { throttleMs = THROTTLE_MS; }
    var lastCall = null;
    return function () {
        var args = [];
        for (var _i = 0; _i < arguments.length; _i++) {
            args[_i] = arguments[_i];
        }
        var now = Date.now();
        if (lastCall === null || (now - lastCall > throttleMs)) {
            fn.apply(this, args);
            lastCall = now;
        }
    };
}
