import React, { useState } from 'react';
import axios from 'axios';
import { Card, TextField, Button } from '@mui/material';

const Pay = () => {
  const [recipientEmail, setRecipientEmail] = useState('');
  const [amount, setAmount] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/api/wallet/pay', { recipientEmail, amount: Number(amount) }, { headers: { Authorization: `Bearer ${token}` } });
      window.location = '/dashboard';
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <Card style={{ width: 400 }}>
        <h2>Pay</h2>
        <form onSubmit={handleSubmit}>
          <TextField label="Recipient Email" fullWidth onChange={(e) => setRecipientEmail(e.target.value)} />
          <TextField type="number" label="Amount" fullWidth onChange={(e) => setAmount(e.target.value)} />
          <Button type="submit">Pay</Button>
        </form>
      </Card>
    </div>
  );
};

export default Pay;
