import React, { useState } from 'react';
import axios from 'axios';
import { Card, TextField, Button } from '@mui/material';

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/register', { name, email, password });
      window.location = '/login';
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <Card style={{ width: 400 }}>
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
          <TextField label="Name" fullWidth onChange={(e) => setName(e.target.value)} />
          <TextField label="Email" fullWidth onChange={(e) => setEmail(e.target.value)} />
          <TextField type="password" label="Password" fullWidth onChange={(e) => setPassword(e.target.value)} />
          <Button type="submit">Register</Button>
        </form>
      </Card>
    </div>
  );
};

export default Register;
