import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Card, Button } from '@mui/material';

const Dashboard = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/user/profile', { headers: { Authorization: `Bearer ${token}` } });
        setUser(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchProfile();
  }, []);

  if (!user) return <div>Loading...</div>;

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <Card style={{ width: 400 }}>
        <h2>Welcome, {user.name}</h2>
        <p>Balance: {user.walletBalance}</p>
        <Link to="/add-money"><Button>Add Money</Button></Link>
        <Link to="/pay"><Button>Pay</Button></Link>
        <Link to="/transactions"><Button>View Transactions</Button></Link>
      </Card>
    </div>
  );
};

export default Dashboard;
