import React, { useState } from 'react';
import axios from 'axios';
import { Card, TextField, Button, Box, Typography } from '@mui/material';
import { Aurora } from 'react-bits';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/auth/login', { email, password });
      localStorage.setItem('token', res.data.token);
      navigate('/dashboard');
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Aurora>
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Card sx={{ width: 400, p: 4, backgroundColor: 'rgba(255, 255, 255, 0.9)' }}>
          <Typography variant="h4" component="h2" sx={{ color: 'primary.main', mb: 3 }}>
            Login
          </Typography>
          <form onSubmit={handleSubmit}>
            <TextField label="Email" fullWidth sx={{ mb: 2 }} onChange={(e) => setEmail(e.target.value)} />
            <TextField type="password" label="Password" fullWidth sx={{ mb: 3 }} onChange={(e) => setPassword(e.target.value)} />
            <Button type="submit" variant="contained" color="secondary" fullWidth>Login</Button>
            <Typography sx={{ mt: 2, textAlign: 'center', color: 'text.secondary' }}>
              Don't have an account? <Link to="/register" style={{ color: '#048cfc' }}>Register</Link>
            </Typography>
          </form>
        </Card>
      </Box>
    </Aurora>
  );
};

export default Login;
